module.exports = function bodyHandler(req, res, next) {
  const chunks = []

  if (req.method == 'POST') {
    req.on('data', function (chunk) {
      chunks.push(chunk)
    })

    req.on('end', function () {
      req.rawBody = Buffer.concat(chunks).toString('latin1')
      next()
    })
  } else {
    next()
  }
}