module.exports = function corsHandler(req,res,next){
  let {url,method} = req
  let origin = req.headers['origin']

  if(method === 'OPTIONS'){

  }else if(isSimpleRequest(req)){
    // 处理简单的cors
    if(origin == 'null'){
      console.log('--')
      res.setHeader('Access-Control-Allow-Origin','*')
    }
  }

  next()
}

function isSimpleRequest(req){
  let {method} = req
  
  return method == 'GET' || method == 'POST' || method == 'HEAD'
}